INSERT INTO `book_details` (`bid`, `title`, `author`, `intro`, `label1`, `label2`, `label3`, `label4`, `classify`, `words`, `readed`, `pic`) VALUES
(1, '骑士征程', '我爱小豆', '\r\n	贵族——平民——奴隶，\r\n亘古不变的等级规则，\r\n实力造就贵族，\r\n底层军官洛克的成长史，\r\n顺应位面晋升大势，\r\n化身入侵者，\r\n开始巫师大陆的骑士征程。\r\n书友Q群：678816703，每日三更。', '骑士', '巫师', '异世', '争霸', '西方奇幻', '3300634', '326600人次', '/images/viewlist_01.jpeg'),
(2, '妖神记', '半根玉溪', '我本躺在市井之中自生自灭，一次偶遇，一见倾心，从而埋头苦修，只为泡妞。神渊大陆修炼分九大境界。武灵、天启、化羽、觉魂、生死、轮回、虚神、圣主、神之极境。', '妖神', '异界', '魔兽', '穿越', '东方玄幻', '231162', '1138221人次', '/images/viewlist_02.jpg'),
(3, '仙道九绝', '剑哮龙翔', '创业青年魂穿到修道世界，来自现代科技的脑瓜子，与修道系统彻底卯上了。雷劫？我有有避雷针！阵法？我会统筹学！剑道？我知道无招胜有招！阴谋诡计？几百部的宫斗剧可不是白看的！各种无厘头，各种打脸，逼得我爆发，就把你们彻底踩在脚下！', '重生', '搞笑', '爽文', '升级', '玄学', '608169', '9010775人次', '/images/viewlist_03.jpeg'),
(4, '最强狂医', '破浪', '不要问我为什么狂，因为我总是最强。我是萧神，是萧不是小，你可以叫我大神。没有我治不好的病，也没有我治不了的女人，我就是，史上最强狂医！', '都市生活', NULL, NULL, NULL, '都市生活', '3089262', '4595人次', '/images/viewlist_04.jpg'),
(5, '史上最强召唤师', '满山骷髅火', '	新书《食妖剑》已开 供应各位书友大驾!', '召唤师', '西方魔法', '楚天', '魔核', '西方奇幻', '835023', '519068人次', '/images/viewlist_05.jpg'),
(6, '龙骸', '死翼耐萨里奥', '在这精彩绝伦的幻想世界里，有着精于铸造的矮人，称霸森林的巨魔，建立帝国的人类，俯瞰苍生的巨龙。一位传奇巨龙陨落，众生为之哀恸；一位人族王者崛起，大陆为之欢呼！巨龙的遗骸，造就佣兵的史诗！', '佣兵', '争霸', '搞笑', '领主', '西方奇幻', '2169214', '2124370人次', '/images/viewlist_06.jpg'),
(7, '科学的魔法世界', '血影修罗', '昔日的巨人文明为了逃避大灾变，以诺亚方舟，前往异世界。 历史轮转，沧海桑田，人类文明的一次偶然，使沉寂的诺亚方舟被再次唤醒，执行了他曾经的使命，秦幽也因此来到了艾尔达世界——一个十分神奇、却又十分科学的——魔法世界！ ‖有青春热学，有阴谋权斗，有魔法奥秘同时也有萌萌哒的萝莉养成以及更多喜闻乐见但此处太小了我写不下的剧情！欢迎收藏点击！', '热血', '穿越', '科幻', '魔法', '西方奇幻', '73400', '23657人次', '/images/viewlist_07.jpeg'),
(8, '刺客学徒', '长叶', '来自地球的贫寒少年，渴望掌握命运而一心追求变强的少年，带着一块神秘的石头，闯入异界的汹涌狂潮之中，掀起一场华丽的风暴。\r\n千年之后，只留下一段关于刺客的传说……（群号：361468355，欢迎加入刺客训练营）', '穿越', '热血', '升级', '异世', '西方奇幻', '787475', '81728人次', '/images/viewlist_08.jpg'),
(9, '神铸', '星九', '当异界再次入侵！\r\n当人界再临危难！\r\n炼器宗的霸器-巨神兵将踏平异界的军队！\r\n炼心将手握自己炼制的超神器守卫自己的家园！\r\n一群真心的朋友将追随在炼心的身后，杀向入侵者的方向！\r\n圣约大陆将在炼心的带领下翻开新的历史篇章，炼器宗在炼心的带领下将走向辉煌！', '神器', '炼器', '炼心', '巨神兵', '西方奇幻', '1742940', '143983人次', '/images/viewlist_09.jpg'),
(10, '重生之绝世至尊', '风一刀', '绝世强者秦破被人陷害，重生在同名武者身上。\r\n修仙者的阅历和经历，让秦破在这个武者的世界如鱼得水。\r\n超凡至圣的各种仙术，遭遇力破万法的武技，注定是一场绚丽的碰撞。\r\n且看秦破纵横三千世界，称霸万法至尊。', '西方奇幻', NULL, NULL, NULL, '西方奇幻', '2667599', '1830人次', '/images/viewlist_10.jpg'),
(11, 'dota杀死卡尔', '猫和雨天', '『在他之前，这个世界上有很多魔法师；\r\n自他以后，便没有人敢以魔法师自居。』\r\n『卡尔，凭借出众的才华颠覆世人对魔法的认知，战乱、纷争、割据皆因他而起……』\r\n『急速冷却，一念之间；灵动迅捷，一瞬之间；毁天灭地，一夕之间……』\r\n『翻手为云，覆手为雨，神罗天征，嗟叹若何……<br/>不过一冰、一雷、一火，一闪而过。』', '爽文', '魔兽', '扮猪吃虎', '争霸', '西方奇幻', '173134', '3375人次', '/images/viewlist_11.jpeg'),
(12, '异界全能法师', '失踪的小狼', '魔法师小号——精神力、魔力满值\r\n得到生活职业大师级的海量知识——全能卷轴\r\n来到了另一个世界——芬里奇多\r\n是穿越？还只是一场梦？\r\n管他妈的、\r\n老子是冲着装13去的、\r\n【群：159249621暂时空虚求插入……喜欢就加吧，没任何要求。】', '搞笑', '爽文', '扮猪吃虎', NULL, '西方奇幻', '72309', '46984人次', '/images/viewlist_12.jpg'),
(13, '大骑士', '提图斯', '古八极拳传人，莫名穿越到异世大陆！罪魁祸首竟然是他的两个哥哥！看他练斗气，创魔法，保家族，风生水起混异世！这一生，注定了他的强大！“骑士，是为了守护而生！”此世成为大骑士的他，将为守护家族，保卫江山而战！', '罗兰', '异世', '穿越', '热血', '西方奇幻', '1072331', '910329人次', '/images/viewlist_13.jpg'),
(14, '重装猎团', '三只小摆', '破碎的魔法与工业时代，残破的世界规则化作无数碎片遗落大地。曾经的盛极一时，如今却只余凋零。这样的世界生活中，我刘白也只是扁舟一叶。\r\n然而，即使是这样渺小的我，也可以有那诺大的梦啊。\r\n想要摆脱体内的残存魔法，依托精准暴力的机械去闯荡。\r\n想要有自己的战车，携手猎团成员战一战现下的混乱。\r\n更甚者，这样破碎的世界，就由我刘白，来重装成乾坤朗朗！', '热血', '末世', '技术流', '生化', '西方奇幻', '128066', '4989人次', '/images/viewlist_14.jpeg'),
(15, '契约神座', '追不逝', '魔法大陆走入尾声，开启魔法世界大战序幕，新书已经在创作之中，此书还在继续更新，持续关注，新书直通车http://book.zongheng.com/book/557273.html', '魔法师', '学徒', '精灵', NULL, '西方奇幻', '5013463', '267559人次', '/images/viewlist_15.jpg'),
(16, '植掌天下', '学徒小弟', '亚特大陆，一个充满魔法与斗气的世界，在这里一切靠实力说话，人们都希望能够成为高尚的魔法师，强大的战士。\r\n紫罗王国四大名望家族之一的克里斯家族，在举行一年一度的家族支脉魔武测试中，来自遥远山村没落的红枫山支脉，年仅九岁的夏凡意外被测出木系魔法元素亲和度超等的资质。\r\n可惜，木系魔法的没落，注定未来无法走的更高、更远、更强。\r\n一根外观如同枯树枝的魔法杖，一个长得道貌岸然的器灵，意外的落入夏凡的手中，接着一切都发生了改变。', '学院', '热血', '种田', '魔兽', '西方奇幻', '141470', '9763人次', '/images/viewlist_16.jpg'),
(17, '帝国鹰扬', '强取胜于苦耕', '奥诺博人的逐渐壮大总是伴随着无限的扩张与鲜血，从默默无闻的小国再到一个举世闻名的帝国，他们前进的路上总是伴随着母亲丧子的哀嚎，以及小孩害怕的哭喊。玛塔利亚帝国成立之后，皇帝又将征服的长剑指向了日落国度—维斯特洛，而这时的维斯特洛也是风起云涌，自从瑟曦上台之后，维斯特洛光辉的一页就已经翻过了，而现在，瑟曦的屁股还没有做暖和，维斯特洛就面临一群以复仇为名的骑士的铁蹄的蹂躏。', '同人', '冰与火之歌', '战争', NULL, '西方奇幻', '4689', '1320人次', '/images/viewlist_17.jpeg'),
(18, '破渊', '执迷知返', '脱离组织的少年，原本只是想过些安静平和的生活，却在命运的裹挟下一次又一次的卷入了大陆的纷争与阴谋之中，这究竟真的是宿命，还是冥冥之中有人操纵了他的命运呢？', '异世', '搞笑', '生活', '扮猪吃虎', '西方奇幻', '126172', '13016人次', '/images/viewlist_18.jpeg'),
(19, '至尊异火', '紫冰雪雨', '异火本生与天地间，异火可以毁天灭地，也可以创造万物。万族林立，群雄并起，诸侯争霸，天骄竞出，问苍茫大地，谁与沉浮。一簇天生异火，看如何掌握乾坤，打破诘难，冲破新生……', '重生', NULL, NULL, NULL, '西方奇幻', '92675', '23341人次', '/images/viewlist_19.jpg'),
(20, '异世孽缘（全）', '红色键盘', '新书《仙道狂神》已经登录书城，往大家多多支持！那一夜，梦中相会　，你是白色无根莲　，我是红色彼岸花　，你苍白如雪　，我妖红似血　，你落落于天山镜池水沄沄　，我寞寞在幽冥黄泉路漫漫　； 那一刻，爱上你　，命里劫数　，无路可逃　，无所可逃　，我会一直等　，三千日斗转星移　，你终于老去　。我依旧沦陷　。 交流群65168352，欢迎大家加入，交流认识 ', '西方奇幻', NULL, NULL, NULL, '西方奇幻', '152075', '17035人次', '/images/viewlist_20.jpg'),
(21, '快穿之攻略美男子', '睡睡平安', '额，取名废。各种奇葩变态，哈哈，有的，美男子，有的。爽文，有的，不定期更，有的。', '好的', '扮猪吃虎', '技术流', '养成', '西方奇幻', '4106', '8886人次', '/images/viewlist_21.jpg'),
(22, '申公豹异界纵横', '元主', '封神之战申公豹一声‘道友请留步’坑杀无数能人异士，当他被元始天尊镇于北海之眼后，却机缘巧合转世重生在异世界大陆。\r\n这里没有神奇道法却有绚丽魔法和强横斗气，美丽的精灵和强壮的兽人，聪明的地精和强大的龙族，看申公豹如何以前世道法纵横这异世大陆，成就一代传奇', '爽文', '穿越', '法宝', '异世', '西方奇幻', '1348382', '42271人次', '/images/viewlist_22.jpeg'),
(23, '骨皇', '怒笑', '前世他是赫赫有名的雇佣兵之王，今生他只是冥界一个微不足道的小骷髅。灯红酒绿纸醉金迷的生活一去不复返，可他会甘心当一个弱者吗？ \r\n“不会！永远不会！我颜修一生宁愿做不得好死的枭雄，决不当苟且偷生的蝼蚁！”', '穿越', '颜修', NULL, NULL, '西方奇幻', '1357847', '542767人次', '/images/viewlist_23.jpg'),
(24, '黑权杖', '死翼耐萨里奥', '当战争不得不发动的时候,战争就是正义的,当武器是我们唯一希望所在的时候,武器就是神圣的。', '西方奇幻', '死翼耐萨里奥', '热血', '争霸', '西方奇幻', '1600699', '2061256人次', '/images/viewlist_24.jpg'),
(25, '加冕为王', '奥丁般纯洁', '他来自于黑暗，用黑暗审判邪恶，追寻光明。终有一日，虚伪的圣光将会熄灭，而他，将加冕为王。', '西幻', '奇幻', '魔法', 'DND', '西方奇幻', '2002359', '265582人次', '/images/viewlist_25.jpeg'),
(26, '荆棘王座', '那落迦', '阿斯莫德大陆永远不缺乏迷人的英雄传说。\r\n不管是神话时代、巨龙时代、精灵时代、巫师时代、骑士时代……永远不缺乏。\r\n哪怕是被称为“恐怖世纪”、“罪恶时代”的现在', '西幻', '魔法', '战争', '巨龙', '西方奇幻', '285730', '6446人次', '/images/viewlist_26.jpeg'),
(27, '格兰小镇来的探险家', '长鹿漫睌', '那一天，卡尔离开格兰小镇，踏上无尽位面流浪之旅。失落的黄金国、破碎的海神三叉戟，死亡海岸神秘的十二水晶骷髅头，水晶棺内的黄金面具，可以实现一切愿望的愿望杯。卡尔找寻着遗失在位面之中一个又一个的令人着迷的宝物。', '位面', '魔幻', '奇幻', '魔兽', '西方奇幻', '310552', '2223人次', '/images/viewlist_27.jpeg'),
(28, '圣魔主宰', '键盘书生', '三次大战，神魔乱舞，妖怪丛生，各种奇兽妖魔发生战斗。　　此时一位西方少年和一位东方少年主宰了这个世界，第四次大战又开始了', '西方奇幻', NULL, NULL, NULL, '西方奇幻', '1835418', '463人次', '/images/viewlist_28.jpg'),
(29, '异界星巫', '树心灯', '穿越到异界的一个默默奋斗的小巫。如果你喜欢我的作品，就请收藏，谢谢各位了。', '魔兽', '生活', '异世', NULL, '西方奇幻', '2023480', '758196人次 ', '/images/viewlist_29.jpg');
